import React, { useEffect } from 'react'
import Card from './card'
import styled from 'styled-components'
import useTestsContext from '../context'
import data from '../../data.json'
import { filter, isEqual, map, sortBy } from 'lodash'

const Container = styled.div`
  display: flex;
  overflow: auto;

  > * {
    margin-right: 24px;
  }

  > :last-child {
    margin-right: 0;
  }
`

const ListOfCards = () => {
  const { tests } = useTestsContext()

  const { ghosts } = data

  useEffect(() => {
    console.log(tests)
    const testsIds = map(tests, (item) => item.id)

    const result = filter(ghosts, (ghost) => {
      return isEqual(sortBy(ghost.evidencesId), sortBy(testsIds))
    })

    console.log(result)
  }, [tests])

  const renderCards = map([0, 1, 2], (_, index) => <Card key={index} />)

  return <Container>{renderCards}</Container>
}

export default ListOfCards
