import React, { useEffect, useState } from 'react'
import Card from './card'
import styled from 'styled-components'
import useTestsContext from '../context'
import data from '../../data.json'
import { filter, isEqual, map, sortBy, isEmpty, take, uniq } from 'lodash'

const Container = styled.div`
  display: flex;
  overflow: auto;
  padding: 16px;

  > * {
    margin-right: 24px;
  }

  > :last-child {
    margin-right: 0;
  }
`

const ListOfCards = () => {
  const { tests } = useTestsContext()

  const [state, setState] = useState(undefined)

  const { ghosts } = data

  useEffect(() => {
    const testsFilter = filter(tests, (test) => !!test)

    const testsIds = map(testsFilter, (item) => item.id)

    if (isEmpty(testsIds) || testsIds.length < 2) {
      setState(undefined)

      return
    }

    const result = filter(ghosts, (ghost) => {
      const ghostEvidencesIds = take(sortBy(ghost.evidencesId), testsIds.length)

      return isEqual(ghostEvidencesIds, sortBy(testsIds))
    })

    setState(result)
  }, [tests])

  const renderCards = map(state, (_, index) => <Card key={index} />)

  return <Container>{renderCards}</Container>
}

export default ListOfCards
