import React from 'react'
import styled, { keyframes } from 'styled-components'

const fadeIn = keyframes`
  0% {opacity:0;}
  100% {opacity:1;}
`

const Image = styled.div`
  background-color: white;
  height: 208px;
`

const SubTitle = styled.h2`
  font-size: 14px;
  margin: 0;
`

const Title = styled.h1`
  font-size: 18px;
  margin: 0;
  text-align: center;
`

const Container = styled.div`
  background-color: #010409;
  border-radius: 8px;
  border: 0.5px solid #f2f2f2;
  color: white;
  max-width: 224px;
  min-width: 224px;
  padding: 24px 16px;
  opacity: 0;
  animation: ${fadeIn} ease 10s;

  > * {
    margin-bottom: 8px;
  }

  > :last-child {
    margin-bottom: 0;
  }
`

const Card = (props) => {
  const { name, difference } = props

  return (
    <Container>
      <Title>{difference}</Title>
      <Image />
      <Title>{name}</Title>
      <SubTitle>Fortalezas</SubTitle>
      <p>
        Puede volar, así que no tocará el suelo en la mayor parte de la partida.
        En algunos casos también traspasa las paredes.
      </p>
      <SubTitle>Debilidades</SubTitle>
      <p>La sal es un elemento que lo intoxica.</p>
    </Container>
  )
}

export default Card
