import React, { useEffect } from 'react'
import Card from './card'
import styled from 'styled-components'
import useTestsContext from '../context'
import data from '../../data.json'
import { filter, isEqual, map, sortBy, find, take } from 'lodash'

const Container = styled.div`
  display: flex;
  overflow: auto;

  > * {
    margin-right: 24px;
  }

  > :last-child {
    margin-right: 0;
  }
`

const ListOfCards = () => {
  const { tests } = useTestsContext()

  const { ghosts } = data

  useEffect(() => {
    const testsFilter = filter(tests, (test) => !!test)

    const testsIds = map(testsFilter, (item) => item.id)

    const result = filter(ghosts, (ghost) => {
      const ghostEvidencesIds = take(sortBy(ghost.evidencesId), testsIds.length)

      return isEqual(ghostEvidencesIds, sortBy(testsIds))
    })
  }, [tests])

  const renderCards = map([0, 1, 2], (_, index) => <Card key={index} />)

  return <Container>{renderCards}</Container>
}

export default ListOfCards
