import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
  height: 25px;
  overflow: hidden;
  width: 25px;

  ${(props) => props.left && `transform: rotate(135deg);`}
`

const Arrow = styled.div`
  background-color: #c4c4c4;
  height: 25px;
  transform: translate(-17px) rotate(45deg);
  width: 25px;

  & :hover {
    box-shadow: 0px 3px 5px 1px black;
    cursor: pointer;
  }
`

const Button = (props) => {
  return (
    <Container>
      <Arrow />
    </Container>
  )
}

export default Button
